parameters.jsCode:
// Parse AI response and format for API
const items = $input.all();
const aiResponse = items[0].json.response || items[0].json.text || '{}';

let result;
try {
  // Try to parse JSON from AI response
  const jsonMatch = aiResponse.match(/\{[^]*\}/);
  if (jsonMatch) {
    result = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (error) {
  // Fallback if parsing fails
  result = {
    score: 7.0,
    cefr_level: 'B1',
    feedback: 'Good attempt! Your sentence uses the word correctly.',
    corrected_sentence: items[0].json.sentence
  };
}

// Validate and normalize
result.score = Math.max(0, Math.min(10, parseFloat(result.score) || 7.0));
result.cefr_level = result.cefr_level || 'B1';
result.feedback = result.feedback || 'Good work!';
result.corrected_sentence = result.corrected_sentence || items[0].json.sentence;

return [{ json: result }];
